    {
        m_ui->m_cmbAuditedDataTable->setEnabled(true);
        m_ui->m_cmbAuditedSizeTable->setEnabled(true);
        m_ui->m_cmbExaminedDataTable->setEnabled(true);
        m_ui->m_cmbExaminedSizeTable->setEnabled(true);
    }
